mapper.select:
SELECT
    *
FROM
    abk_u_cache
WHERE
    template_id = #{template_id}
AND sa_prefix = #{sa_prefix}
AND group_name = #{group_name}

mapper.update:
UPDATE abk_u_cache SET
instance_url = #{instance_url},
instance_status = #{instance_status},
file_name = #{file_name},
update_date = DATE_FORMAT(NOW(), '%Y-%m-%d %T')
WHERE
    template_id = #{template_id}
AND sa_prefix = #{sa_prefix}
AND group_name = #{group_name}
